import { useEffect, useState } from 'react';
import Tippy from '@tippyjs/react/headless';

import 'tippy.js/dist/tippy.css'; // optional
import classNames from 'classnames/bind';
import {} from '@fortawesome/fontawesome-svg-core';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
    faCircleXmark,
    faSpinner,
    faMagnifyingGlass,
    faPlus,
    faEllipsisVertical,
    faUpLong,
    faMoon,
    faQuestionCircle,
    faCircleDot,
    faGlobe,
    faEllipsisH,
    faChevronLeft,
    faLocationArrow,
    faMessage,
    faUser,
    faRightFromBracket,
} from '@fortawesome/free-solid-svg-icons';

import { Wrapper as PopperWrapper } from '~/components/Popper';
import AccountItem from '~/components/AccountItem';
import Button from '~/components/Button';
import styles from './Header.module.scss';
import images from '~/assets/images';
import Menu from '~/components/Popper/Menu';

const cx = classNames.bind(styles);
const MENU_ITEMS = [
    {
        icon: <FontAwesomeIcon icon={faUser} />,
        title: 'Xem hồ sơ',
        type: 'isLogin',
    },
    {
        icon: <FontAwesomeIcon icon={faRightFromBracket} />,
        title: 'Đăng xuất',
        type: 'isLogin',
    },
    {
        icon: <FontAwesomeIcon icon={faGlobe} />,
        title: 'Tiếng Việt',
        children: {
            title: 'Ngôn ngữ',
            icon: <FontAwesomeIcon icon={faChevronLeft} />,
            data: [
                {
                    code: 'en',
                    title: 'English',
                },
                {
                    code: 'vi',
                    title: 'Tiếng Việt',
                },
            ],
        },
    },
    {
        icon: <FontAwesomeIcon icon={faQuestionCircle} />,
        title: 'Phản hồi và trợ giúp',
    },
    {
        icon: <FontAwesomeIcon icon={faEllipsisH} />,
        title: 'Phím tắt ',
    },
    {
        icon: <FontAwesomeIcon icon={faMoon} />,
        title: 'Chế độ tối',
    },
];
function Header() {
    const [searchResult, setSearchResult] = useState([]);
    useEffect(() => {
        setTimeout(() => {
            setSearchResult([]);
        }, 0);
    }, []);
    const isLogin = true;
    return (
        <header className={cx('wrapper')}>
            <div className={cx('inner')}>
                <div className={cx('logo')}>
                    <img src={images.logo} alt="Tiktok" />
                </div>
                <Tippy
                    interactive
                    visible={searchResult.length > 0}
                    render={(attrs) => (
                        <div className={cx('search-result')} tabIndex="-1" {...attrs}>
                            <PopperWrapper>
                                <div className={cx('search-title')}>Tài khoản</div>
                                <AccountItem />
                                <AccountItem />
                                <AccountItem />
                                <AccountItem />
                                <AccountItem />
                                <AccountItem />
                            </PopperWrapper>
                        </div>
                    )}
                >
                    <div className={cx('search')}>
                        <input type="text" placeholder="Tìm kiếm" spellCheck={false} />
                        <div className={cx('clear')}>
                            {/* <button className={cx('clear-btn')}>
                            <FontAwesomeIcon icon={faCircleXmark} />
                        </button> */}
                            <FontAwesomeIcon className={cx('spinner')} icon={faSpinner} spin />
                        </div>
                        <span className={cx('spliter')} />

                        <button className={cx('search-btn')}>
                            <FontAwesomeIcon icon={faMagnifyingGlass} />
                        </button>
                    </div>
                </Tippy>

                <div className={cx('actions')}>
                    <Button outline leftIcon={<FontAwesomeIcon icon={faPlus} />}>
                        Tải lên
                    </Button>
                    <Tippy
                        render={(attrs) => (
                            <div className="box" tabIndex="-1" {...attrs}>
                                My tippy box
                            </div>
                        )}
                    >
                        <button className={cx('action-btn')}>
                            <FontAwesomeIcon icon={faLocationArrow} />
                        </button>
                    </Tippy>
                    <Tippy
                        render={(attrs) => (
                            <div className="box" tabIndex="-1" {...attrs}>
                                My tippy box
                            </div>
                        )}
                    >
                        <button className={cx('action-btn')}>
                            <FontAwesomeIcon icon={faMessage} />
                        </button>
                    </Tippy>
                    <button className={cx('action-btn')}>
                        <FontAwesomeIcon icon={faEllipsisVertical} />
                    </button>

                    {/* {isLogin ? (
                        <>
                            <Tippy
                                visible
                                render={(attrs) => (
                                    <div className="box" tabIndex="-1" {...attrs}>
                                        Tin nhắn
                                    </div>
                                )}
                            >
                                <Button icon={<FontAwesomeIcon icon={faLocationArrow} />} />
                            </Tippy>
                            <Tippy content="Hộp thư">
                                <Button icon={<FontAwesomeIcon icon={faMessage} />} />
                            </Tippy>
                        </>
                    ) : (
                        <>
                            <Button primary>Đăng nhập</Button>
                        </>
                    )}
                    <Menu items={MENU_ITEMS} isLogin>
                        {isLogin ? (
                            <button className={cx('avatar-btn')}>
                                <img src={images.logo99} alt="avatar" className={cx('avatar')} />
                            </button>
                        ) : (
                            <button className={cx('more-btn')}>
                                <FontAwesomeIcon icon={faEllipsisVertical} />
                            </button>
                        )}
                    </Menu> */}
                </div>
            </div>
        </header>
    );
}

export default Header;
