import { useEffect, useState } from 'react';
import Tippy from '@tippyjs/react/headless';

import 'tippy.js/dist/tippy.css'; // optional
import classNames from 'classnames/bind';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
    faCircleXmark,
    faSpinner,
    faMagnifyingGlass,
    faPlus,
    faEllipsisVertical,
    faUpLong,
} from '@fortawesome/free-solid-svg-icons';

import { Wrapper as PopperWrapper } from '~/components/Popper';
import styles from './Header.module.scss';
import images from '~/assets/images';

const cx = classNames.bind(styles);
function Header() {
    const [searchResult, setSearchResult] = useState([]);
    useEffect(() => {
        setTimeout(() => {
            setSearchResult([1, 2, 3, 4, 5, 6]);
        }, 0);
    }, []);
    return (
        <header className={cx('wrapper')}>
            <div className={cx('inner')}>
                <div className={cx('logo')}>
                    <img src={images.logo} alt="Tiktok" />
                </div>
                <Tippy
                    visible
                    render={(attrs) => (
                        <div className="box" tabIndex="-1" {...attrs}>
                            My tippy box
                        </div>
                    )}
                >
                    <button>My button</button>
                </Tippy>
                <div className={cx('search')}>
                    <input type="text" placeholder="Tìmđá kiếm" spellCheck={false} />
                    <div className={cx('clear')}>
                        {/* <button className={cx('clear-btn')}>
                            <FontAwesomeIcon icon={faCircleXmark} />
                        </button> */}
                        <FontAwesomeIcon className={cx('spinner')} icon={faSpinner} spin />
                    </div>
                    <span className={cx('spliter')} />

                    <button className={cx('search-btn')}>
                        <FontAwesomeIcon icon={faMagnifyingGlass} />
                    </button>
                </div>

                <div className={cx('actions')}>
                    <div>
                        <a>
                            <div className={cx('upload-btn')}>
                                <FontAwesomeIcon icon={faPlus} />
                                <span>Tải lên</span>
                            </div>
                        </a>
                    </div>
                    <button className={cx('login-btn')}>Đăng nhập</button>
                    <div className={cx('more-menu')}>
                        <FontAwesomeIcon icon={faEllipsisVertical} />
                        {/* <div className={cx('po */}
                    </div>
                </div>
            </div>
        </header>
    );
}

export default Header;
